import React, { Component, useState , useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import CourseList from "./courseList";
import "./Home";
const SingleGenEd = () => {
const Courses = (props) => (
    <tr>
      <td>
        <h1>Hello</h1>
      <Link to={`/details/${props.course._id}`} key={props.course._id}> {props.course.Course} </Link></td>
      <td>{props.course['Course Title']}</td>
    </tr>
  );

function CourseLists() {
    const [courses, setCourses] = useState([]);

    // This method fetches the records from the database.
    useEffect(() => {
      async function getCourses() {
        const response = await fetch(`http://localhost:5000/courses/`);

        if (!response.ok) {
          const message = `An error occurred: ${response.statusText}`;
          window.alert(message);
          return;
        }

        const courses = await response.json();
        setCourses(courses);
      }

      getCourses();

      return;
    }, [courses.length]);

    // This method will map out the records on the table
    function courseLists() {
      return courses.map((course) => {
        return <Courses course={course} key={course._id} />;
      });
    }

    // This following section will display the table with the records of courses.
    return (
      <div>
        <table className='table table-striped' style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Numbers</th>
              <th>Name</th>
            </tr>
          </thead>
          <tbody>{courseLists()}</tbody>
        </table>
      </div>
    );
  }
    const params = useParams();
    const courseid = params.courseId //adn then look up and fetch data from there
        return (
            <div>
            <image src="https://i.pinimg.com/236x/b8/9e/28/b89e287af55113b8f25349a1c94815c1.jpg" alt="Click to return to home screen"/> 
            <h1>{courseid} ClassName {props.course.Course}</h1>
            <h3> Course Description: pulls from the data</h3>
            <h3> Average Rating</h3> 
            <image src="https://icon2.cleanpng.com/20171220/ofw/star-png-image-5a3a7e9651e390.02167008151378293433541614.jpg" alt="display image"/> 
            <h3> Number of Ratings: </h3>
            <h3> Categories: import the gen-ed categories</h3> 
            </div>
            );
}
export default SingleGenEd;

