import '../stylesheets/Search.css';
import React, { Component, useState } from 'react';
import Course from './courseList';
import CourseList from './courseList';
export default function Search() {
  const [courses, setCourses] = useState([]);
  const [searchValue, setSearchValue] = useState([]);

  handleOnChange = (event) => {
    // this.setState({ searchValue: event.target.value });
    setSearchValue(event.target.value);
  };

  courseList = () => {
    return this.state.courses.map((course) => {
      return <Course course={course} key={course._id} />;
    });
  };

  handleSearch = () => {
    console.log('this is what you typed: ', this.state.searchValue);
    this.getFilteredCourses(this.state.searchValue);
  };

  getFilteredCourses = (searchInput) => {
    async function getCourses() {
      //this is bad right now copied from another file
      const response = await fetch(
        `http://localhost:5000/courses/${searchInput}`
      );

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const courses = await response.json();
      this.state.courses = courses;
    }

    getCourses();
  };

  return (
    <div>
      <h1>Rate My Gen-Eds @ UIUC</h1>
      <p>This is what you typed: {this.state.searchValue}</p>
      <input
        name='text'
        type='text'
        placeholder='Search'
        onChange={(event) => this.handleOnChange(event)}
        value={this.state.searchValue}
      />

      <button onClick={this.handleSearch}>Search</button>
      <h1>{this.courseList()}</h1>
    </div>
  );
}
